import * as PIXI from 'pixi.js';
import { PLANET_RENDERER_CONFIGS } from './rendererModels';
import { SHIPS } from '../simulation/simulationModels';
import { ObjectPool } from './utils';

class ShipRenderer {
  constructor(ship) {
    this.textures = {
      [SHIPS.SMALL.id]: PIXI.Texture.from('orbital-bombardment-simulator/orbital-bombardment-simulator/assets/small_ship.png'),
      [SHIPS.MEDIUM.id]: PIXI.Texture.from('orbital-bombardment-simulator/orbital-bombardment-simulator/assets/medium_ship.png'),
      [SHIPS.LARGE.id]: PIXI.Texture.from('orbital-bombardment-simulator/orbital-bombardment-simulator/assets/big_ship.png'),
    };
    this.sprite = new PIXI.Sprite(this.textures[ship.type.id]);
    this.sprite.anchor.set(0.5);
    this.updateSprite(ship);
  }

  updateSprite(ship) {
    if (!ship || !ship.type) return;
    this.sprite.texture = this.textures[ship.type.id];

    switch(ship.type.id) {
      case SHIPS.SMALL.id:
        this.sprite.scale.set(0.5);
        break;
      case SHIPS.MEDIUM.id:
        this.sprite.scale.set(0.75);
        break;
      case SHIPS.LARGE.id:
        this.sprite.scale.set(1);
        break;
      default:
        console.warn(`Unexpected ship type: ${ship.type.id}`);
        this.sprite.scale.set(1);
    }
  }

  render(ship) {
    this.sprite.x = ship.x;
    this.sprite.y = ship.y;
    this.sprite.rotation = ship.angle + Math.PI / 2;
  }
}

class BulletRenderer {
  constructor(bullet) {
    this.sprite = new PIXI.Graphics();
    this.updateSprite(bullet);
  }

  updateSprite(bullet) {
    this.sprite.clear();
    this.sprite.beginFill(0xFFFF00);
    this.sprite.drawCircle(0, 0, 2);
    this.sprite.endFill();
  }

  render(bullet) {
    this.sprite.x = bullet.x;
    this.sprite.y = bullet.y;
  }
}

class PlanetRenderer {
  constructor(planet, x, y) {
    this.planet = planet;
    this.x = x;
    this.y = y;
    this.rotation = 0;

    this.planetSprite = new PIXI.Graphics();
    this.healthBarSprite = new PIXI.Graphics();
    this.healthBarSprite.x = x - 50;
    this.healthBarSprite.y = y + 60;

    this.nameText = new PIXI.Text(planet.name, {
      fontFamily: "'Exo', sans-serif",
      fontSize: 18,
      fill: 0xFFFFFF,
      align: 'center'
    });
    this.nameText.anchor.set(0.5);
    this.nameText.x = x;
    this.nameText.y = y - 70;

    this.updateSprite();
  }

  updateSprite() {
    const renderType = PLANET_RENDERER_CONFIGS[this.planet.id.toUpperCase()];
    const radius = Math.random() * (renderType.maxRadius - renderType.minRadius) + renderType.minRadius;
    const craterCount = Math.floor(Math.random() * (renderType.maxCraterCount - renderType.minCraterCount + 1)) + renderType.minCraterCount;

    this.planetSprite.clear();
    this.planetSprite.beginFill(renderType.color);
    this.planetSprite.drawCircle(0, 0, radius);
    this.planetSprite.endFill();

    this.planetSprite.beginFill(0x000000, renderType.craterOpacity);
    for (let i = 0; i < craterCount; i++) {
      const craterAngle = Math.random() * Math.PI * 2;
      const craterDistance = Math.random() * (radius * renderType.craterDistanceFactor);
      const craterX = Math.cos(craterAngle) * craterDistance;
      const craterY = Math.sin(craterAngle) * craterDistance;
      const craterRadius = Math.random() * (radius * (renderType.maxCraterSize - renderType.minCraterSize)) + (radius * renderType.minCraterSize);
      this.planetSprite.drawCircle(craterX, craterY, craterRadius);
    }
    this.planetSprite.endFill();
  }

  render(health) {
    this.planetSprite.x = this.x;
    this.planetSprite.y = this.y;
    this.planetSprite.rotation = this.rotation;

    this.healthBarSprite.clear();
    this.healthBarSprite.beginFill(0x00FF00);
    this.healthBarSprite.drawRect(0, 0, (health / this.planet.maxHealth) * 100, 10);
    this.healthBarSprite.endFill();

    this.nameText.text = this.planet.name;
  }

  updateRotation(deltaTime) {
    const renderType = PLANET_RENDERER_CONFIGS[this.planet.id.toUpperCase()];
    this.rotation += renderType.rotationSpeed * deltaTime;
  }
}

export class GameRenderer {
  constructor(container, width, height, initialPlanet) {
    this.width = width;
    this.height = height;
    this.app = new PIXI.Application({
      width,
      height,
      backgroundColor: 0x000000,
    });
    container.appendChild(this.app.view);

    this.background = PIXI.Sprite.from('orbital-bombardment-simulator/orbital-bombardment-simulator/assets/game_background.png');
    this.background.width = width;
    this.background.height = height;
    this.app.stage.addChild(this.background);

    this.shipRenderers = new Map();
    this.bulletRenderers = new Map();
    this.planetRenderer = new PlanetRenderer(initialPlanet, width / 2, height / 2);

    this.app.stage.addChild(this.planetRenderer.planetSprite);
    this.app.stage.addChild(this.planetRenderer.healthBarSprite);
    this.app.stage.addChild(this.planetRenderer.nameText);

    this.shipPool = new ObjectPool(() => new ShipRenderer({}));
    this.bulletPool = new ObjectPool(() => new BulletRenderer({}));
    this.planetPool = new ObjectPool(() => new PlanetRenderer({}, width / 2, height / 2));

    this.lastUpdateTime = Date.now();
  }

  getShipRenderer(ship) {
    if (!this.shipRenderers.has(ship)) {
      const renderer = this.shipPool.get();
      renderer.updateSprite(ship);
      this.app.stage.addChild(renderer.sprite);
      this.shipRenderers.set(ship, renderer);
    }
    return this.shipRenderers.get(ship);
  }

  getBulletRenderer(bullet) {
    if (!this.bulletRenderers.has(bullet)) {
      const renderer = this.bulletPool.get();
      renderer.updateSprite(bullet);
      this.app.stage.addChild(renderer.sprite);
      this.bulletRenderers.set(bullet, renderer);
    }
    return this.bulletRenderers.get(bullet);
  }

  getPlanetRenderer(planet) {
    if (this.planetRenderer.planet !== planet) {
      this.app.stage.removeChild(this.planetRenderer.planetSprite);
      this.app.stage.removeChild(this.planetRenderer.healthBarSprite);
      this.app.stage.removeChild(this.planetRenderer.nameText);

      this.planetPool.release(this.planetRenderer);
      this.planetRenderer = this.planetPool.get();
      this.planetRenderer.planet = planet;
      this.planetRenderer.updateSprite();

      this.app.stage.addChild(this.planetRenderer.planetSprite);
      this.app.stage.addChild(this.planetRenderer.healthBarSprite);
      this.app.stage.addChild(this.planetRenderer.nameText);
    }
    return this.planetRenderer;
  }

  removeShipRenderer(ship) {
    const renderer = this.shipRenderers.get(ship);
    if (renderer) {
      this.app.stage.removeChild(renderer.sprite);
      this.shipPool.release(renderer);
      this.shipRenderers.delete(ship);
    }
  }

  removeBulletRenderer(bullet) {
    const renderer = this.bulletRenderers.get(bullet);
    if (renderer) {
      this.app.stage.removeChild(renderer.sprite);
      this.bulletPool.release(renderer);
      this.bulletRenderers.delete(bullet);
    }
  }

  updatePlanetRotation() {
    const currentTime = Date.now();
    const deltaTime = (currentTime - this.lastUpdateTime) / 1000;
    this.planetRenderer.updateRotation(deltaTime);
    this.lastUpdateTime = currentTime;
  }

  render(simulation) {
    this.updatePlanetRotation();

    simulation.ships.forEach(ship => {
      const renderer = this.getShipRenderer(ship);
      renderer.render(ship);
    });

    this.shipRenderers.forEach((renderer, ship) => {
      if (!simulation.ships.includes(ship)) {
        this.removeShipRenderer(ship);
      }
    });

    simulation.bullets.forEach(bullet => {
      const renderer = this.getBulletRenderer(bullet);
      renderer.render(bullet);
    });

    this.bulletRenderers.forEach((renderer, bullet) => {
      if (!simulation.bullets.includes(bullet)) {
        this.removeBulletRenderer(bullet);
      }
    });

    const currentPlanet = simulation.getCurrentPlanet();
    const planetRenderer = this.getPlanetRenderer(currentPlanet);
    planetRenderer.render(simulation.getPlanetHealth());
  }

  destroy() {
    this.app.destroy(true);
  }
}
